        <?php
        function get_categories_and_courses()
        {
            $category_ids = [52601, 54051, 54243, 23506, 51872, 51873, 51870, 54203, 53139, 52565, 53136];

            $categories = get_terms([
                'taxonomy' => 'course-cat',
                'hide_empty' => true,
                'include' => $category_ids,
                'number' => 11,
            ]);

            $data = [];
            foreach ($categories as $category) {
                $courses = new WP_Query([
                    'post_type' => 'course',
                    'tax_query' => [
                        [
                            'taxonomy' => 'course-cat',
                            'field' => 'term_id',
                            'terms' => $category->term_id,

                        ],
                    ],
                    'posts_per_page' => 7,
                ]);


                $data[] = [
                    'category' => $category,
                    'courses' => $courses->posts,
                ];
            }

            return $data;
        }


        $categories_and_courses = get_categories_and_courses();
